import React from 'react'
import { Spring } from 'react-spring'
import styled, { keyframes } from 'styled-components'

const Primary = styled.h1`
    margin: 50px;
    letter-spacing: 8px;
    text-transform: uppercase;
    line-height: 95%;
    font-size: 4rem;
    color: ${props => props.primary ? "#6f203d;" : "white"};
    font-family: 'Oswald', sans-serif;
`
const Secondary = styled.h2`
  color:purple;
  margin-left: 50px;
`

export default function Body(){
  return (
    <React.Fragment>
      <Primary primary>We’ve built a better way to deliver fresh blooms. </Primary>
      <Secondary>At BloomsyBox, we think it should be simple for everyone to receive and enjoy fresh, beautiful blooms whenever and wherever they want: at home, at work, and everywhere in-between. We do things different... in a good way! Instead of the traditional mixed bouquet, we’re sending simple yet chic single-variety bunches,
      delivered straight from the source. </Secondary>
    </React.Fragment>
  )
}
